styles:
.panel-header {
  display: flex;
}
.panel-heading {
  margin: 0px;
  line-height: 30px;
  font-weight: 100;
}
.panel-toolbar-right {
  margin-left: auto;
}
.panel-toolbar-left,
.panel-toolbar-right {
  display: flex;
}

.toolbar-item {
  color: #757575;
  display: inline-block;
  padding: 3px;
  text-align: center;
  font-size: 1.3em;
  transition: all 0.4s;
}
.toolbar-item i {
  transition: all 0.4s;
  font-size: 1.5rem;
  padding: 0.2em;
}
.toolbar-item:hover {
  background-color: #e8e8e8;
}
.toolbar-item:focus {
  background-color: #e8e8e8;
}

.footer span {
  vertical-align: middle;
}
.footer-right-segment a {
  margin-left: 5px;
  margin-right: 5px;
}
.footer-right-segment {
  margin-right: 10px;
  float: right;
}
.footer-left-segment {
  margin-left: 15px;
  float: left;
}
.footer-left-segment a {
  color: #000000;
}

:host {
  display: block;
  height: 36px;
}
.wrapper {
  position: absolute;
  opacity: 1;
}
.wrapper.noshow {
  -webkit-transition: opacity 1s linear;
  -moz-transition: opacity 1s linear;
  -o-transition: opacity 1s linear;
  transition: opacity 1s linear;
  opacity: 0;
}
svg {
  animation: 2s linear infinite svg-animation;
  width: 36px;
}

.noshow svg {
  animation: none;
}

@keyframes svg-animation {
  0% {
    transform: rotateZ(0deg);
  }
  100% {
    transform: rotateZ(360deg);
  }
}

circle {
  animation: 1.4s ease-in-out infinite both circle-animation;
  display: block;
  fill: transparent;
  stroke: #ffffff;
  stroke-linecap: round;
  stroke-dasharray: 283;
  stroke-dashoffset: 280;
  stroke-width: 10px;
  transform-origin: 50% 50%;
}

@keyframes circle-animation {
  0%,
  25% {
    stroke-dashoffset: 280;
    transform: rotate(0);
  }

  50%,
  75% {
    stroke-dashoffset: 75;
    transform: rotate(45deg);
  }

  100% {
    stroke-dashoffset: 280;
    transform: rotate(360deg);
  }
}

.code {
  display: block;
  clear: both;
  padding: 0.5em;
  font-family: monospace;
  background-color: #ececec;
  border-radius: 4px;
}
